mapper.update:
update d_board
set
title=#{title}, content=#{content} ,image=#{image}, regdate=sysdate,
ip = #{ip}
where bno=#{bno}

mapper.select:
select bno, title, nickname, regdate, readcnt, email,image,category
from (select rownum rnum, bno, title, nickname, regdate,
	readcnt, email ,image,category
	from (select * from d_board order by regdate desc))
where rnum >= #{pageStart} and 
      rnum <= #{pageStart} + #{perPageNum} - 1
